import React from 'react';
import { FaBars, FaTimes } from 'react-icons/fa'; // For the hamburger menu

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className="fixed top-0 left-0 w-full z-50 bg-transparent backdrop-blur-md text-white shadow-md">
      <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
        {/* Logo or Name */}
        <div className="text-xl font-bold text-pink-500">Ayush Yadav</div>

        {/* Desktop Menu */}
        <div className="hidden md:flex space-x-4">
          <a href="#home" className="hover:text-pink-400 transition">Home</a>
          <a href="#projects" className="hover:text-pink-400 transition">Projects</a>
          <a href="#skills" className="hover:text-pink-400 transition">Skills</a>
          <a href="#connect" className="hover:text-pink-400 transition">Connect</a>
        </div>

        {/* Mobile Menu (Hamburger) */}
        <div className="md:hidden">
          <button onClick={handleMenuToggle}>
            {isMenuOpen ? (
              <FaTimes className="text-3xl text-pink-500" />
            ) : (
              <FaBars className="text-3xl text-pink-500" />
            )}
          </button>
        </div>
      </div>

      {/* Mobile Dropdown Menu */}
      {isMenuOpen && (
        <div className="md:hidden bg-gray-800 text-white p-4">
          <a href="#home" className="block py-2 hover:text-pink-400 transition">Home</a>
          <a href="#projects" className="block py-2 hover:text-pink-400 transition">Projects</a>
          <a href="#skills" className="block py-2 hover:text-pink-400 transition">Skills</a>
          <a href="#connect" className="block py-2 hover:text-pink-400 transition">Connect</a>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
