import React from 'react';
import { FaLinkedin, FaGithub, FaEnvelope } from 'react-icons/fa';
import { TypeAnimation } from 'react-type-animation';

const Home = () => {
  return (
    <div className="bg-gray-900 text-white min-h-screen flex flex-col justify-center items-center">
      <div className="max-w-6xl w-full flex flex-col md:flex-row items-center justify-between px-4 py-20">
        
        {/* Left Section */}
        <div className="md:w-1/2 text-center md:text-left space-y-6">
          <h1 className="text-4xl sm:text-6xl font-bold leading-tight">
            <TypeAnimation
              sequence={[
                "Hello, it's me\n",
                1500,
                "Hello, it's me\nAyush Yadav",
                1500,
                "Hello, it's me\nA Data Geek",
                2000,
              ]}
              wrapper="span"
              speed={50}
              repeat={Infinity}
              style={{ whiteSpace: 'pre-line', display: 'inline-block' }}
            />
          </h1>

          <p className="text-gray-300 text-lg">
            I’m a passionate data geek who turns raw numbers into impactful insights.
            Skilled in data analysis, Python, and visualization — aiming to solve real-world problems through data.
          </p>

          {/* Social Icons */}
          <div className="flex gap-6 justify-center md:justify-start">
            <a href="https://linkedin.com" target="_blank" rel="noreferrer">
              <FaLinkedin className="text-3xl hover:text-pink-500 transition" />
            </a>
            <a href="https://github.com" target="_blank" rel="noreferrer">
              <FaGithub className="text-3xl hover:text-pink-500 transition" />
            </a>
            <a href="mailto:youremail@example.com">
              <FaEnvelope className="text-3xl hover:text-pink-500 transition" />
            </a>
          </div>

          {/* CV Download */}
          <div className="mt-6">
            <a
              href="/Ayush_Yadav_CV.pdf"
              download
              className="bg-pink-600 hover:bg-pink-700 text-white px-6 py-2 rounded-full transition"
            >
              Download CV
            </a>
          </div>
        </div>

        {/* Right Section - Profile Picture */}
        <div className="relative p-1 bg-gradient-to-tr from-purple-500 via-blue-500 to-pink-500 rounded-full animate-border bg-[length:400%_400%] w-72 h-72 md:w-[26rem] md:h-[26rem] transition-transform duration-300 hover:scale-110 hover:shadow-[0_0_40px_8px_rgba(236,72,153,0.7)]">
          <div className="w-full h-full bg-gray-900 rounded-full p-1">
            <img
              src="/profile.jpg"
              alt="Profile"
              className="w-full h-full object-cover rounded-full border-4 border-gray-900"
            />
          </div>
        </div>

      </div>
    </div>
  );
};

export default Home;
