import React, { useState } from 'react';

const LetsConnect = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted:", formData);
    alert('Thank you for reaching out!');
    setFormData({
      name: '',
      email: '',
      phone: '',
      message: ''
    });
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center px-4 py-16">
      <h1 className="text-4xl md:text-5xl font-bold mb-10">Let's Connect</h1>
      <form onSubmit={handleSubmit} className="bg-gray-800 p-8 rounded-lg w-full max-w-xl shadow-lg space-y-6">
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          className="w-full p-3 rounded bg-gray-700 text-white placeholder-gray-400 focus:outline-none"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Your Email"
          className="w-full p-3 rounded bg-gray-700 text-white placeholder-gray-400 focus:outline-none"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="tel"
          name="phone"
          placeholder="Your Phone Number"
          className="w-full p-3 rounded bg-gray-700 text-white placeholder-gray-400 focus:outline-none"
          value={formData.phone}
          onChange={handleChange}
        />
        <textarea
          name="message"
          placeholder="Your Message"
          rows="4"
          className="w-full p-3 rounded bg-gray-700 text-white placeholder-gray-400 focus:outline-none"
          value={formData.message}
          onChange={handleChange}
          required
        ></textarea>
        <button
          type="submit"
          className="w-full bg-pink-600 hover:bg-pink-700 py-3 rounded text-white font-semibold transition"
        >
          Send Message
        </button>
      </form>
    </div>
  );
};

export default LetsConnect;
